import * as React from 'react';
import { useChartId } from "../hooks/useChartId.js";
import { getValueToPositionMapper, useLineSeriesContext, useXAxes, useYAxes } from "../hooks/index.js";
import { cleanId } from "../internals/cleanId.js";
import { DEFAULT_X_AXIS_KEY } from "../constants/index.js";
import getColor from "./seriesConfig/getColor.js";
import { useChartContext } from "../context/ChartProvider/index.js";
export function useMarkPlotData(xAxes, yAxes) {
  const seriesData = useLineSeriesContext();
  const defaultXAxisId = useXAxes().xAxisIds[0];
  const defaultYAxisId = useYAxes().yAxisIds[0];
  const chartId = useChartId();
  const {
    instance
  } = useChartContext();
  const allData = React.useMemo(() => {
    if (seriesData === undefined) {
      return [];
    }
    const {
      series,
      stackingGroups
    } = seriesData;
    const markPlotData = [];
    for (const stackingGroup of stackingGroups) {
      const groupIds = stackingGroup.ids;
      for (const seriesId of groupIds) {
        const {
          xAxisId = defaultXAxisId,
          yAxisId = defaultYAxisId,
          stackedData,
          data,
          showMark = true,
          shape = 'circle'
        } = series[seriesId];
        if (showMark === false) {
          continue;
        }
        if (!(xAxisId in xAxes) || !(yAxisId in yAxes)) {
          continue;
        }
        const xScale = getValueToPositionMapper(xAxes[xAxisId].scale);
        const yScale = yAxes[yAxisId].scale;
        const xData = xAxes[xAxisId].data;
        if (process.env.NODE_ENV !== 'production') {
          if (xData === undefined) {
            throw new Error(`MUI X Charts: ${xAxisId === DEFAULT_X_AXIS_KEY ? 'The first `xAxis`' : `The x-axis with id "${xAxisId}"`} should have data property to be able to display a line plot.`);
          }
        }
        const clipId = cleanId(`${chartId}-${seriesId}-line-clip`);
        const colorGetter = getColor(series[seriesId], xAxes[xAxisId], yAxes[yAxisId]);
        const marks = [];
        if (xData) {
          for (let index = 0; index < xData.length; index += 1) {
            const x = xData[index];
            const value = data[index] == null ? null : stackedData[index][1];
            if (value === null) {
              continue;
            }
            const y = yScale(value);
            const xPos = xScale(x);
            if (!instance.isPointInside(xPos, y)) {
              continue;
            }
            if (showMark !== true) {
              const shouldInclude = showMark({
                x: xPos,
                y,
                index,
                position: x,
                value
              });
              if (!shouldInclude) {
                continue;
              }
            }
            marks.push({
              x: xPos,
              y,
              index,
              color: colorGetter(index)
            });
          }
        }
        markPlotData.push({
          seriesId,
          clipId,
          shape,
          xAxisId,
          marks
        });
      }
    }
    return markPlotData;
  }, [seriesData, defaultXAxisId, defaultYAxisId, chartId, xAxes, yAxes, instance]);
  return allData;
}